using System.Threading.Tasks;
using Nethereum.ABI.FunctionEncoding.Attributes;
using Nethereum.Contracts;
using Nethereum.Hex.HexTypes;
using Nethereum.Web3;

namespace Nethereum.ENS
{
    public class EnsService
    {
        public static string ABI =
            @"[{'constant':true,'inputs':[{'name':'node','type':'bytes32'}],'name':'resolver','outputs':[{'name':'','type':'address'}],'payable':false,'type':'function'},{'constant':true,'inputs':[{'name':'node','type':'bytes32'}],'name':'owner','outputs':[{'name':'','type':'address'}],'payable':false,'type':'function'},{'constant':false,'inputs':[{'name':'node','type':'bytes32'},{'name':'label','type':'bytes32'},{'name':'owner','type':'address'}],'name':'setSubnodeOwner','outputs':[],'payable':false,'type':'function'},{'constant':false,'inputs':[{'name':'node','type':'bytes32'},{'name':'ttl','type':'uint64'}],'name':'setTTL','outputs':[],'payable':false,'type':'function'},{'constant':true,'inputs':[{'name':'node','type':'bytes32'}],'name':'ttl','outputs':[{'name':'','type':'uint64'}],'payable':false,'type':'function'},{'constant':false,'inputs':[{'name':'node','type':'bytes32'},{'name':'resolver','type':'address'}],'name':'setResolver','outputs':[],'payable':false,'type':'function'},{'constant':false,'inputs':[{'name':'node','type':'bytes32'},{'name':'owner','type':'address'}],'name':'setOwner','outputs':[],'payable':false,'type':'function'},{'inputs':[],'type':'constructor'},{'anonymous':false,'inputs':[{'indexed':true,'name':'node','type':'bytes32'},{'indexed':true,'name':'label','type':'bytes32'},{'indexed':false,'name':'owner','type':'address'}],'name':'NewOwner','type':'event'},{'anonymous':false,'inputs':[{'indexed':true,'name':'node','type':'bytes32'},{'indexed':false,'name':'owner','type':'address'}],'name':'Transfer','type':'event'},{'anonymous':false,'inputs':[{'indexed':true,'name':'node','type':'bytes32'},{'indexed':false,'name':'resolver','type':'address'}],'name':'NewResolver','type':'event'},{'anonymous':false,'inputs':[{'indexed':true,'name':'node','type':'bytes32'},{'indexed':false,'name':'ttl','type':'uint64'}],'name':'NewTTL','type':'event'}]";

        public static string BYTE_CODE =
            "";

        private readonly Web3.Web3 web3;

        private readonly Contract contract;

        public EnsService(Web3.Web3 web3, string address)
        {
            this.web3 = web3;
            contract = web3.Eth.GetContract(ABI, address);
        }

        public static Task<string> DeployContractAsync(Web3.Web3 web3, string addressFrom, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            return web3.Eth.DeployContract.SendRequestAsync(ABI, BYTE_CODE, addressFrom, gas, valueAmount);
        }

        public Event GetEventNewOwner()
        {
            return contract.GetEvent("NewOwner");
        }

        public Event GetEventNewResolver()
        {
            return contract.GetEvent("NewResolver");
        }

        public Event GetEventNewTTL()
        {
            return contract.GetEvent("NewTTL");
        }

        public Event GetEventTransfer()
        {
            return contract.GetEvent("Transfer");
        }

        public Function GetFunctionOwner()
        {
            return contract.GetFunction("owner");
        }

        public Function GetFunctionResolver()
        {
            return contract.GetFunction("resolver");
        }

        public Function GetFunctionSetOwner()
        {
            return contract.GetFunction("setOwner");
        }

        public Function GetFunctionSetResolver()
        {
            return contract.GetFunction("setResolver");
        }

        public Function GetFunctionSetSubnodeOwner()
        {
            return contract.GetFunction("setSubnodeOwner");
        }

        public Function GetFunctionSetTTL()
        {
            return contract.GetFunction("setTTL");
        }

        public Function GetFunctionTtl()
        {
            return contract.GetFunction("ttl");
        }

        public Task<string> OwnerAsyncCall(byte[] node)
        {
            var function = GetFunctionOwner();
            return function.CallAsync<string>(node);
        }

        public Task<string> ResolverAsyncCall(byte[] node)
        {
            var function = GetFunctionResolver();
            return function.CallAsync<string>(node);
        }

        public Task<string> SetOwnerAsync(string addressFrom, byte[] node, string owner, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            var function = GetFunctionSetOwner();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, node, owner);
        }

        public Task<string> SetResolverAsync(string addressFrom, byte[] node, string resolver, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            var function = GetFunctionSetResolver();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, node, resolver);
        }

        public Task<string> SetSubnodeOwnerAsync(string addressFrom, byte[] node, byte[] label, string owner,
            HexBigInteger gas = null, HexBigInteger valueAmount = null)
        {
            var function = GetFunctionSetSubnodeOwner();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, node, label, owner);
        }

        public Task<string> SetTTLAsync(string addressFrom, byte[] node, ulong ttl, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            var function = GetFunctionSetTTL();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, node, ttl);
        }

        public Task<ulong> TtlAsyncCall(byte[] node)
        {
            var function = GetFunctionTtl();
            return function.CallAsync<ulong>(node);
        }
    }

    public class NewOwnerEventDTO
    {
        [Parameter("bytes32", "node", 1, true)]
        public byte[] Node { get; set; }

        [Parameter("bytes32", "label", 2, true)]
        public byte[] Label { get; set; }

        [Parameter("address", "owner", 3, false)]
        public string Owner { get; set; }
    }

    public class TransferEventDTO
    {
        [Parameter("bytes32", "node", 1, true)]
        public byte[] Node { get; set; }

        [Parameter("address", "owner", 2, false)]
        public string Owner { get; set; }
    }

    public class NewResolverEventDTO
    {
        [Parameter("bytes32", "node", 1, true)]
        public byte[] Node { get; set; }

        [Parameter("address", "resolver", 2, false)]
        public string Resolver { get; set; }
    }

    public class NewTTLEventDTO
    {
        [Parameter("bytes32", "node", 1, true)]
        public byte[] Node { get; set; }

        [Parameter("uint64", "ttl", 2, false)]
        public ulong Ttl { get; set; }
    }
}