using System.Threading.Tasks;
using Nethereum.Contracts;
using Nethereum.Hex.HexTypes;
using Nethereum.Web3;

namespace Nethereum.ENS
{
    public class FIFSRegistrarService
    {
        public static string ABI =
            @"[{'constant':false,'inputs':[{'name':'subnode','type':'bytes32'},{'name':'owner','type':'address'}],'name':'register','outputs':[],'payable':false,'type':'function'},{'inputs':[{'name':'ensAddr','type':'address'},{'name':'node','type':'bytes32'}],'type':'constructor'}]";

        public static string BYTE_CODE =
            "";

        private readonly Web3.Web3 web3;

        private readonly Contract contract;

        public FIFSRegistrarService(Web3.Web3 web3, string address)
        {
            this.web3 = web3;
            contract = web3.Eth.GetContract(ABI, address);
        }

        public static Task<string> DeployContractAsync(Web3.Web3 web3, string addressFrom, string ensAddr, byte[] node,
            HexBigInteger gas = null, HexBigInteger valueAmount = null)
        {
            return web3.Eth.DeployContract
                .SendRequestAsync(ABI, BYTE_CODE, addressFrom, gas, valueAmount, ensAddr, node);
        }

        public Function GetFunctionRegister()
        {
            return contract.GetFunction("register");
        }

        public Task<string> RegisterAsync(string addressFrom, byte[] subnode, string owner, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            var function = GetFunctionRegister();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, subnode, owner);
        }
    }
}