using System.Threading.Tasks;
using Nethereum.Contracts;
using Nethereum.Hex.HexTypes;
using Nethereum.Web3;

namespace Nethereum.ENS
{
    public class PublicResolverService
    {
        public static string ABI =
            @"[{'constant':true,'inputs':[{'name':'interfaceID','type':'bytes4'}],'name':'supportsInterface','outputs':[{'name':'','type':'bool'}],'payable':false,'type':'function'},{'constant':true,'inputs':[{'name':'node','type':'bytes32'}],'name':'content','outputs':[{'name':'ret','type':'bytes32'}],'payable':false,'type':'function'},{'constant':true,'inputs':[{'name':'node','type':'bytes32'}],'name':'addr','outputs':[{'name':'ret','type':'address'}],'payable':false,'type':'function'},{'constant':true,'inputs':[{'name':'node','type':'bytes32'},{'name':'kind','type':'bytes32'}],'name':'has','outputs':[{'name':'','type':'bool'}],'payable':false,'type':'function'},{'constant':false,'inputs':[{'name':'node','type':'bytes32'},{'name':'hash','type':'bytes32'}],'name':'setContent','outputs':[],'payable':false,'type':'function'},{'constant':false,'inputs':[{'name':'node','type':'bytes32'},{'name':'addr','type':'address'}],'name':'setAddr','outputs':[],'payable':false,'type':'function'},{'inputs':[{'name':'ensAddr','type':'address'}],'type':'constructor'},{'payable':false,'type':'fallback'}]";

        public static string BYTE_CODE =
            "";

        private readonly Web3.Web3 web3;

        private readonly Contract contract;

        public PublicResolverService(Web3.Web3 web3, string address)
        {
            this.web3 = web3;
            contract = web3.Eth.GetContract(ABI, address);
        }

        public Task<string> AddrAsyncCall(byte[] node)
        {
            var function = GetFunctionAddr();
            return function.CallAsync<string>(node);
        }

        public Task<byte[]> ContentAsyncCall(byte[] node)
        {
            var function = GetFunctionContent();
            return function.CallAsync<byte[]>(node);
        }

        public static Task<string> DeployContractAsync(Web3.Web3 web3, string addressFrom, string ensAddr,
            HexBigInteger gas = null, HexBigInteger valueAmount = null)
        {
            return web3.Eth.DeployContract.SendRequestAsync(ABI, BYTE_CODE, addressFrom, gas, valueAmount, ensAddr);
        }

        public Function GetFunctionAddr()
        {
            return contract.GetFunction("addr");
        }

        public Function GetFunctionContent()
        {
            return contract.GetFunction("content");
        }

        public Function GetFunctionHas()
        {
            return contract.GetFunction("has");
        }

        public Function GetFunctionSetAddr()
        {
            return contract.GetFunction("setAddr");
        }

        public Function GetFunctionSetContent()
        {
            return contract.GetFunction("setContent");
        }

        public Function GetFunctionSupportsInterface()
        {
            return contract.GetFunction("supportsInterface");
        }

        public Task<bool> HasAsyncCall(byte[] node, byte[] kind)
        {
            var function = GetFunctionHas();
            return function.CallAsync<bool>(node, kind);
        }

        public Task<string> SetAddrAsync(string addressFrom, byte[] node, string addr, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            var function = GetFunctionSetAddr();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, node, addr);
        }

        public Task<string> SetContentAsync(string addressFrom, byte[] node, byte[] hash, HexBigInteger gas = null,
            HexBigInteger valueAmount = null)
        {
            var function = GetFunctionSetContent();
            return function.SendTransactionAsync(addressFrom, gas, valueAmount, node, hash);
        }

        public Task<bool> SupportsInterfaceAsyncCall(byte[] interfaceID)
        {
            var function = GetFunctionSupportsInterface();
            return function.CallAsync<bool>(interfaceID);
        }
    }
}